import React from 'react';
import './ResultDisplay.css'; // Optional for styling

const ResultDisplay = ({ source, destination, shortestPaths }) => {
  return (
    <div className="result-display-container">
      {shortestPaths ? (
        <div>
          <h3>Shortest Path from {source} to {destination}</h3>
          <p>
            {shortestPaths[destination] === Infinity
              ? 'Destination is Unreachable'
              : `Shortest Distance: ${shortestPaths[destination]}`}
          </p>
        </div>
      ) : (
        <p>No results to display yet.</p>
      )}
    </div>
  );
};

export default ResultDisplay;
