import React, { useState } from 'react';
import './DijkstraForm.css';
const DijkstraForm = ({ onSubmit }) => {
  const [graph, setGraph] = useState('');
  const [source, setSource] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Parse graph input into a proper format
    const parsedGraph = graph.split('\n').map((row) => row.split(',').map(Number));
    onSubmit(parsedGraph, parseInt(source, 10));
  };

  return (
    <div>
      <h2>Enter Graph Data</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Graph (edges as comma-separated rows, e.g. 0,1,2,3):
          <textarea
            value={graph}
            onChange={(e) => setGraph(e.target.value)}
            placeholder="Enter graph as rows of comma-separated numbers"
          />
        </label>
        <br />
        <label>
          Source Node:
          <input
            type="number"
            value={source}
            onChange={(e) => setSource(e.target.value)}
            placeholder="Enter source node"
          />
        </label>
        <br />
        <button type="submit">Run Dijkstra</button>
      </form>
    </div>
  );
};

export default DijkstraForm;
