import React, { useState } from 'react';
import '../styles/DijkstraForm.css';

const DijkstraForm = ({ onSubmit }) => {
  const [graph, setGraph] = useState('');
  const [source, setSource] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Parse graph input into a proper format
    const parsedGraph = graph.split('\n').map((row) => row.split(',').map(Number));
    onSubmit(parsedGraph, parseInt(source, 10));
  };

  return (
    <div className="dijkstra-form-container">
      <h2 className="dijkstra-form-title">Enter Graph Data</h2>
      <form onSubmit={handleSubmit} className="dijkstra-form">
        <label className="dijkstra-form-label">
          Graph (edges as comma-separated rows, e.g. 0,1,2,3):
          <textarea
            value={graph}
            onChange={(e) => setGraph(e.target.value)}
            placeholder="Enter graph as rows of comma-separated numbers"
            className="dijkstra-form-textarea"
          />
        </label>
        <br />
        <label className="dijkstra-form-label">
          Source Node:
          <input
            type="number"
            value={source}
            onChange={(e) => setSource(e.target.value)}
            placeholder="Enter source node"
            className="dijkstra-form-input"
          />
        </label>
        <br />
        <button type="submit" className="dijkstra-form-button">
          Run Dijkstra
        </button>
      </form>
    </div>
  );
};

export default DijkstraForm;
